    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Conexion controlador = new Conexion(); 
        String sql = "SELECT * FROM EMPLEADO;";
        boolean exito = controlador.consulta(sql); 
        if(exito) { 
            jTextArea2.setText(controlador.getHileraResultado()); 
            jComboBox1.removeAllItems(); 
            controlador.llenarCombobox(sql,jComboBox1);
            controlador.llenarTabla(sql, jTable1); 
            
        } else { 
        System.out.println("No se ejecutó la consulta correctamente"); 
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        Conexion controlador = new Conexion(); 
        String sql = "SELECT NumeroOrden, IdentificacionEmpleado, Salon, NumeroMesa, HoraCreacion FROM ORDEN ORDER BY NumeroOrden;";
        boolean exito = controlador.consulta(sql); 
        if(exito) { 
            jTextArea2.setText(controlador.getHileraResultado()); 
            jComboBox1.removeAllItems(); 
            controlador.llenarCombobox(sql,jComboBox1);
            controlador.llenarTabla(sql, jTable1); 
        } else { 
        System.out.println("No se ejecutó la consulta correctamente"); 
        }
    }//GEN-LAST:event_jButton3ActionPerformed

